import React, {useState} from 'react'

export default function Textform(props) {
    const [text, settext] = useState("Enter text here");

    const onclickfire = () => {
            console.log(text);
            let newtext = text.toUpperCase;
            settext(newtext)
            
    }

    const onchangefire = (Event) => {
        console.log("onchangefire");
        settext(Event.target.value)
    }
    return (
        
        <>
        
        
            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" value={text} onChange={onchangefire} id="mybox" rows="8"></textarea>
            </div>
            <button className="btn btn-primary" onClick={onclickfire}>Convert To Uppercase</button>
        </>
    )
}
