import React, { useState } from 'react'

export default function Textform(props) {
    const [text, settext] = useState("Enter text here");

    const onclickupfire = () => {
        // console.log(text);
        let newtext = text.toUpperCase();
        settext(newtext);

    }

    const onclicklofire = () => {
        // console.log(text);
        let newtext = text.toLowerCase();
        settext(newtext);

    }

  

    const onchangefire = (Event) => {
        // console.log("onchangefire");
        settext(Event.target.value)
    }

    return (

        <>

            <div className="container">
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={onchangefire} id="mybox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary mr-3 mb-3" onClick={onclickupfire}>Convert To Uppercase</button>
                <button className="btn btn-primary mr-3 mb-3" onClick={onclicklofire}>Convert To lowercase</button>
                <button className="btn btn-primary mr-3 mb-3" onclick>Convert To Bold</button>

            </div>
            <div className="container">
                <h2>Your text summary</h2>
                <p>{text.split(" ").length} words and {text.length} characters</p>
                <p>{0.008 * text.split(" ").length} Minutes Read</p>
                <h2>Preview</h2>
                <p>{text}</p>
            </div>
        </>
    )
}
